-- ----------------------------
-- Table structure for geic_banner
-- ----------------------------
DROP TABLE IF EXISTS `geic_banner`;
CREATE TABLE `geic_banner`  (
  `banner_id` int(11) NOT NULL AUTO_INCREMENT COMMENT 'banner主键ID',
  `banner_name` varchar(100) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '名称',
  `banner_position` varchar(100) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT 'Banner位置',
  `banner_desc` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '简介',
  `banner_url` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT 'banner图存储地址',
  `banner_video_url` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT 'banner视频地址',
  `banner_type` tinyint(2) NULL DEFAULT NULL COMMENT 'banner类型',
  `banner_redirect` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '跳转地址',
  `status` tinyint(2) NULL DEFAULT NULL COMMENT '状态0编辑1发布2未发布3删除',
  `sort` tinyint(2) NULL DEFAULT NULL COMMENT '顺序',
  `create_id` int(11) NULL DEFAULT NULL COMMENT '创建人ID',
  `create_time` datetime NULL DEFAULT NULL COMMENT '创建时间',
  `update_id` int(11) NULL DEFAULT NULL COMMENT '更新人ID',
  `update_time` datetime NULL DEFAULT NULL COMMENT '更新时间',
  PRIMARY KEY (`banner_id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 5 CHARACTER SET = utf8 COLLATE = utf8_general_ci COMMENT = 'banner管理' ROW_FORMAT = Dynamic;

