DROP TABLE IF EXISTS `geic_class_manage`;
CREATE TABLE `geic_class_manage`  (
  `class_id` int(11) NOT NULL AUTO_INCREMENT COMMENT '分类ID',
  `class_type` tinyint(2) NULL DEFAULT NULL COMMENT '分类类型（01软件超市分类02供需查询区03产品分类04解决方案分类05软件超市查询区06企业行业分类07企业类型）',
  `class_name` varchar(100) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '分类名称',
  `class_icon` varchar(100) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '分类简图（首页软件展示）',
  `class_desc` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '分类简述',
  `parent_class_id` int(11) NULL DEFAULT NULL COMMENT '父分类ID',
  `class_level` tinyint(2) NULL DEFAULT NULL COMMENT '分类级别',
  `class_order` tinyint(2) NULL DEFAULT NULL COMMENT '顺序',
  `status` tinyint(2) NULL DEFAULT NULL COMMENT '状态0删除1正常',
  `create_id` int(11) NULL DEFAULT NULL COMMENT '创建人ID',
  `create_time` datetime NULL DEFAULT NULL COMMENT '创建时间',
  `update_id` int(11) NULL DEFAULT NULL COMMENT '更新人ID',
  `update_time` datetime NULL DEFAULT NULL COMMENT '更新时间',
  PRIMARY KEY (`class_id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 10010106 CHARACTER SET = utf8 COLLATE = utf8_general_ci COMMENT = '分类管理' ROW_FORMAT = Dynamic;

