-- Table structure for geic_demand_bid
-- ----------------------------
DROP TABLE IF EXISTS `geic_demand_bid`;
CREATE TABLE `geic_demand_bid`  (
  `bid_id` int(11) NOT NULL AUTO_INCREMENT COMMENT '竞标ID（一各需求可选多个中标）',
  `demand_id` int(11) NOT NULL COMMENT '需求ID',
  `user_id` int(11) NOT NULL COMMENT '需求竞标企业ID',
  `company_name` varchar(100) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '企业名称',
  `offer` decimal(10, 2) NULL DEFAULT NULL COMMENT '需求竞标报价(-1面议正数为实际报价)',
  `reason` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '需求竞标理由',
  `mobile` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '联系方式',
  `is_bid` tinyint(2) NULL DEFAULT NULL COMMENT '是否中标（0进行中 1成功 2失败）',
  `status` tinyint(2) NULL DEFAULT NULL COMMENT '(0撤销1正常)',
  `create_time` datetime NULL DEFAULT NULL COMMENT '创建时间',
  `update_time` datetime NULL DEFAULT NULL COMMENT '更新时间',
  PRIMARY KEY (`bid_id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 100044 CHARACTER SET = utf8 COLLATE = utf8_general_ci COMMENT = '需求竞标表' ROW_FORMAT = Dynamic;

-- ----------------------------
-- Table structure for geic_doc
-- ----------------------------
DROP TABLE IF EXISTS `geic_doc`;
CREATE TABLE `geic_doc`  (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `doc_url` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL,
  `doc_name` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT '' COMMENT '文档名称',
  `doc_image` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '文档图片',
  `doc_desc` varchar(511) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '文件简介',
  `status` tinyint(2) NULL DEFAULT NULL COMMENT '状态0删除1发布2未发布',
  `create_time` datetime NULL DEFAULT NULL COMMENT '创建时间',
  `update_time` datetime NULL DEFAULT NULL COMMENT '修改时间',
  `create_id` int(11) NULL DEFAULT NULL COMMENT '创建人ID',
  `update_id` int(11) NULL DEFAULT NULL COMMENT '修改人',
  `remark` varchar(1000) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '备注',
  `pubdate` datetime NULL DEFAULT NULL,
  PRIMARY KEY (`id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 4 CHARACTER SET = utf8 COLLATE = utf8_general_ci COMMENT = '推荐资料下载关系表' ROW_FORMAT = Dynamic;

