-- ----------------------------
-- Table structure for geic_news
-- ----------------------------
DROP TABLE IF EXISTS `geic_news`;
CREATE TABLE `geic_news`  (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '新闻资讯主键ID',
  `title` varchar(100) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '标题',
  `image` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '标题图片',
  `news_desc` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '简介',
  `news_content` text CHARACTER SET utf8 COLLATE utf8_general_ci NULL COMMENT '内容',
  `type` tinyint(2) NULL DEFAULT NULL COMMENT '资讯类型（0新闻、1政策、2资料）',
  `url_type` tinyint(2) NULL DEFAULT NULL COMMENT '链接地址类型（在线、下载）',
  `url_address` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '链接地址或下载地址',
  `pubdate` datetime NULL DEFAULT NULL COMMENT '发布时间',
  `status` tinyint(2) NULL DEFAULT NULL COMMENT '状态0删除1发布2未发布',
  `keyword` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '关键词',
  `create_id` int(11) NULL DEFAULT NULL COMMENT '创建人ID',
  `create_time` datetime NULL DEFAULT NULL COMMENT '创建时间',
  `update_id` int(11) NULL DEFAULT NULL COMMENT '更新人ID',
  `update_time` datetime NULL DEFAULT NULL COMMENT '更新时间',
  PRIMARY KEY (`id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 10103 CHARACTER SET = utf8 COLLATE = utf8_general_ci COMMENT = '新闻资讯表' ROW_FORMAT = Dynamic;

