-- ----------------------------
-- Table structure for geic_online_message
-- ----------------------------
DROP TABLE IF EXISTS `geic_online_message`;
CREATE TABLE `geic_online_message`  (
  `msg_id` int(11) NOT NULL AUTO_INCREMENT COMMENT '留言主键ID',
  `title` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '留言标题',
  `name` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '姓名',
  `mobile` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '电话',
  `email` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '邮箱',
  `content` varchar(500) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '留言内容',
  `msg_time` datetime NULL DEFAULT NULL COMMENT '留言时间',
  `status` tinyint(2) NULL DEFAULT NULL COMMENT '状态0未处理1已处理',
  `operate_time` datetime NULL DEFAULT NULL COMMENT '留言处理时间',
  `operate_id` int(11) NULL DEFAULT NULL COMMENT '留言处理人ID',
  `msg_userId` int(11) NULL DEFAULT NULL COMMENT '留言人ID（游客为空）',
  PRIMARY KEY (`msg_id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 10036 CHARACTER SET = utf8 COLLATE = utf8_general_ci COMMENT = '在线留言表' ROW_FORMAT = Dynamic;

-- ----------------------------
-- Table structure for geic_online_order
-- ----------------------------
DROP TABLE IF EXISTS `geic_online_order`;
CREATE TABLE `geic_online_order`  (
  `order_id` int(11) NOT NULL AUTO_INCREMENT COMMENT '预约ID',
  `product_id` int(11) NOT NULL COMMENT '产品ID',
  `user_id` int(11) NOT NULL COMMENT '预约操作用户ID',
  `product_type` tinyint(2) NULL DEFAULT NULL COMMENT '产品类型（0软件超市1产品2解决方案）',
  `order_name` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '预约人名称',
  `order_mobile` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '预约人电话',
  `order_email` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '预约人邮箱',
  `order_desc` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '预约内容描述',
  `order_time` datetime NULL DEFAULT NULL COMMENT '预约时间',
  `order_status` tinyint(2) NULL DEFAULT NULL COMMENT '预约状态0删除1未处理2已处理',
  `update_id` int(11) NULL DEFAULT NULL COMMENT '更新人ID',
  `update_time` datetime NULL DEFAULT NULL COMMENT '更新时间（用户删除等更新操作）',
  `operate_time` datetime NULL DEFAULT NULL COMMENT '操作时间',
  `operate_id` int(11) NULL DEFAULT NULL COMMENT '操作人ID（后台对预约信息处理操作）',
  PRIMARY KEY (`order_id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 51 CHARACTER SET = utf8 COLLATE = utf8_general_ci COMMENT = '在线预约表' ROW_FORMAT = Dynamic;

-- ----------------------------
-- Table structure for geic_online_service
-- ----------------------------
DROP TABLE IF EXISTS `geic_online_service`;
CREATE TABLE `geic_online_service`  (
  `chat_id` int(11) NOT NULL AUTO_INCREMENT COMMENT '聊天记录ID',
  `user_id` int(11) NULL DEFAULT NULL COMMENT '用户ID',
  `user_name` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '用户姓名',
  `send_id` int(11) NULL DEFAULT NULL COMMENT '发送人ID',
  `send_time` datetime NULL DEFAULT NULL COMMENT '发送时间',
  `history_content` text CHARACTER SET utf8 COLLATE utf8_general_ci NULL COMMENT '聊天内容',
  `send_name` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '发送人姓名',
  PRIMARY KEY (`chat_id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 1 CHARACTER SET = utf8 COLLATE = utf8_general_ci COMMENT = '在线客服记录表' ROW_FORMAT = Dynamic;

-- ----------------------------
-- Table structure for geic_online_service_library
-- ----------------------------
DROP TABLE IF EXISTS `geic_online_service_library`;
CREATE TABLE `geic_online_service_library`  (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '知识库ID',
  `parent_id` int(11) NULL DEFAULT NULL COMMENT '父知识库ID',
  `type` tinyint(2) NULL DEFAULT NULL COMMENT '类型0知识节点1知识内容',
  `keyword` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '关键词',
  `content` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '知识库内容',
  `sort` tinyint(2) NULL DEFAULT NULL COMMENT '顺序',
  `status` tinyint(2) NULL DEFAULT NULL COMMENT '状态0在用1删除',
  `title` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '知识库标题',
  `create_id` int(11) NULL DEFAULT NULL COMMENT '创建人ID',
  `create_time` datetime NULL DEFAULT NULL COMMENT '创建时间',
  `update_id` int(11) NULL DEFAULT NULL COMMENT '更新人ID',
  `update_time` datetime NULL DEFAULT NULL COMMENT '更新时间',
  PRIMARY KEY (`id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 1 CHARACTER SET = utf8 COLLATE = utf8_general_ci COMMENT = '在线客服知识库表' ROW_FORMAT = Dynamic;

