-- ----------------------------
-- Table structure for geic_product_application
-- ----------------------------
DROP TABLE IF EXISTS `geic_product_application`;
CREATE TABLE `geic_product_application`  (
  `application_id` int(11) NOT NULL AUTO_INCREMENT COMMENT '应用场景ID',
  `product_id` int(11) NOT NULL COMMENT '产品ID',
  `application_title` varchar(100) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '应用场景标题',
  `application_image` varchar(100) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '应用场景图标',
  `application_content` text CHARACTER SET utf8 COLLATE utf8_general_ci NULL COMMENT '应用场景内容',
  `sort` tinyint(2) NULL DEFAULT NULL COMMENT '顺序',
  `status` tinyint(2) NULL DEFAULT NULL COMMENT '状态',
  `create_id` int(11) NULL DEFAULT NULL COMMENT '创建人ID',
  `create_time` datetime NULL DEFAULT NULL COMMENT '创建时间',
  `update_id` int(11) NULL DEFAULT NULL COMMENT '更新人ID',
  `update_time` datetime NULL DEFAULT NULL COMMENT '更新时间',
  PRIMARY KEY (`application_id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 28 CHARACTER SET = utf8 COLLATE = utf8_general_ci COMMENT = '产品-应用场景表' ROW_FORMAT = Compact;

-- ----------------------------
-- Records of geic_product_application
-- ----------------------------
INSERT INTO `geic_product_application` VALUES (1, 300101, '环境监测', '/upload/image/product/product-a1.png', '可用于工厂、工地、仓储、医院、学校、商业区等场景的环境监测。通过传感器和摄像头全方位、全天候检测环境数据，如：温度、湿度、噪音、污染物（PM2.5、PM10）、有害气体（甲醛和挥发物）等。支持政府、企业以及民众以不同的视图使用环境监测应用。', 1, 1, 100001, '2018-07-04 14:38:54', NULL, NULL);
INSERT INTO `geic_product_application` VALUES (2, 300101, '智能制造', '/upload/image/product/product-a2.png', '可用于工厂等场景的产品质量检验，如缺陷检测。通过摄像头检测生产线上组装完成的产品是否存在缺陷，对良品和次品图像进行采集，根据采集的图像数据训练模型，检测出次品触发告警。', 2, 1, 100001, '2018-07-04 14:38:54', NULL, NULL);
INSERT INTO `geic_product_application` VALUES (3, 300101, '冷链监测', '/upload/image/product/product-a3.png', '可用于血液、疫苗、药品、样本等存储或运输场景的冷链监测。通过温湿度、GPS传感器实时监测冷链车、冷链仓库和保温箱等的温湿度以及对应的位置信息，设置报警触发器，对异常情况进行报警。', 3, 1, 100001, '2018-07-04 14:38:54', NULL, NULL);
INSERT INTO `geic_product_application` VALUES (4, 300102, '石油化工巡检', '/upload/image/product/product-a2_1.png', '石化行业管道距离较远且布局繁琐，炼塔及操作平台高度较高，个别人员作业区域属于有毒有害或密闭空间，巡检过程耗时较长。应用智能巡检产品，保持设备安全、经济高效运行，提升维保人员工作责任心和管理水平。', 1, 1, 100001, '2018-07-04 14:38:54', NULL, NULL);
INSERT INTO `geic_product_application` VALUES (5, 300102, '电力线路巡检', '/upload/image/product/product-a2_2.png', '传统的电力运维及管理模式已不能适应智能电网快速发展的需求，通过智能巡检产品对输电环节、变电环节、配电环节实现全面的智能化的巡检和监测，提高预警能力和信息化水平；', 2, 1, 100001, '2018-07-04 14:38:54', NULL, NULL);
INSERT INTO `geic_product_application` VALUES (6, 300102, '工厂设备点检', '/upload/image/product/product-a2_3.png', '为了确保工厂大型设备不间断的运行，避免意外停机或机组设备事故的发生，在管理上必须采取定时的设备巡检方式对设备进行日常管理和维护。传统的人工点检往往不能满足目前设备量以及企业发展需求，各类工厂也在探索采用现代化的巡检手段以提升巡检效率。', 3, 1, 100001, '2018-07-04 14:38:54', NULL, NULL);
INSERT INTO `geic_product_application` VALUES (7, 300102, '煤矿安全巡检', '/upload/image/product/product-a2_4.png', '煤矿安全事故频发，主要包括瓦斯爆炸、塌方、渗水。究其原因主要是没有按照规定进行严格的安全检查，从而导致有问题没有及时发现、处理，以致造成严重后果。智能巡检产品可应对现有在线瓦斯安全报警系统的局限和传统巡检模式的弊端，提高安全保障。', 4, 1, 100001, '2018-07-04 14:38:54', NULL, NULL);
INSERT INTO `geic_product_application` VALUES (8, 300104, '桌面式设备 ', '/upload/image/product/product-a4_1.png', '设备轻便、成本低廉，适用于小微企业、临时办公地点等安全网络接入，支持4G无线通信能力', 1, 1, 100001, '2018-07-04 14:38:54', NULL, NULL);
INSERT INTO `geic_product_application` VALUES (9, 300104, '机架式设备', '/upload/image/product/product-a4_2.png', '强大的处理能力，丰富的虚拟专网、网络安全及网络管控功能，成本适中，适用于大中型企业，快速组建虚拟专用网络，满足其他增值服务', 2, 1, 100001, '2018-07-04 14:38:54', NULL, NULL);
INSERT INTO `geic_product_application` VALUES (10, 300104, '云化部署', '/upload/image/product/product-a4_3.png', '软件功能云化，用户侧无需部署硬件，实现云网协同、云端管控、云化安全，信息化水平高，免去硬件部署，适用于私有云、公有云及有云化需求的企业', 3, 1, 100001, '2018-07-04 14:38:54', NULL, NULL);
INSERT INTO `geic_product_application` VALUES (17, 300103, '工业内网：数控机床互联', '/upload/image/product/product-a3_1.png', '面向制造类企业，实现生产设备运行状态可视化监测，和数字化反向控制', 1, 1, 100001, '2018-07-04 14:38:54', NULL, NULL);
INSERT INTO `geic_product_application` VALUES (18, 300103, '工业外网：机电设备互联', '/upload/image/product/product-a3_2.png', '面向钢铁、煤矿，实现机电设备可视化状态监测和全生命周期的维保服务', 1, 1, 100001, '2018-07-04 14:38:54', NULL, NULL);
INSERT INTO `geic_product_application` VALUES (19, 300105, '多种IP VPN部署', '/upload/image/product/product-a4_3.png', '在厂区网络出口处部署SD-WAN网关，一台网关集成IPsec、SSL、L2TP、GRE等多种VPN，企业管理员通过集中管理平台进行操作，傻瓜式、一次性配置各节点VPN功能。', 1, 1, 100001, '2018-09-19 11:01:11', NULL, NULL);
INSERT INTO `geic_product_application` VALUES (20, 300105, '网络安全管控', '/upload/image/product/product-a4_3.png', '企业管理员通过集中管理平台向子公司SD-WAN网关配置入侵检测、防病毒功能、DDoS功能等安全功能，企业管理员可随时对功能进行增、删、改操作。', 2, 1, 100001, '2018-09-19 11:01:11', NULL, NULL);
INSERT INTO `geic_product_application` VALUES (21, 300105, '网络精细化管理', '/upload/image/product/product-a4_3.png', '在企业网络出口处部署SD-WAN网关，通过管理平台，统一配置网络探针和DPI功能，设置流量协议黑白名单，以及异常报警阈值。', 3, 1, 100001, '2018-09-19 11:01:12', NULL, NULL);
INSERT INTO `geic_product_application` VALUES (22, 300105, '低成本MPLS VPN', '/upload/image/product/product-a4_3.png', '在总部、分支机构出口处分别部署SD-WAN网关，通过集中管理平台，配置各网关的MPLS-VPN功能，完成MPLS虚拟专网的搭建，有效降低组网成本。', 4, 1, 100001, '2018-09-19 11:01:12', NULL, NULL);
INSERT INTO `geic_product_application` VALUES (23, 300105, '云网一体化', '/upload/image/product/product-a4_3.png', '在公有云、私有云上实现SD-WAN软件功能云化部署，打通私有云和公有云之间的隧道，组建混合云，形成云-云、云-站点间一体化组网。', 5, 1, 100001, '2018-09-19 11:01:12', NULL, NULL);
INSERT INTO `geic_product_application` VALUES (24, 300105, ' 园区带宽动态调整（BOD）', '/upload/image/product/product-a4_3.png', '通过登录集中管理平台，提交带宽调整信息，无需向运营商申请。通过在线自助操作和开通，可对网络带宽进行动态调整。', 6, 1, 100001, '2018-09-19 11:01:12', NULL, NULL);
INSERT INTO `geic_product_application` VALUES (25, 300106, '油气管道巡检', '/upload/image/product/product-a2_1.png', '自动配置巡检任务，并通过终端实时推送给巡检人员。巡检人员根据任务及规定路线在规定时间内进行巡检时，进行实时路线导航。', 1, 1, 100001, '2018-09-19 11:25:38', NULL, NULL);
INSERT INTO `geic_product_application` VALUES (26, 300106, '电力线路巡检', '/upload/image/product/product-a2_2.png', '可精确定位巡检人员的作业位置。对第三方破坏、线路塔架异常、绝缘子破损等情况（点选式故障问题）进行实时上报，对故障进行检修处理。', 2, 1, 100001, '2018-09-19 11:25:40', NULL, NULL);
INSERT INTO `geic_product_application` VALUES (27, 300106, '厂内设备点检', '/upload/image/product/product-a2_3.png', '班组长预先进行点检任务计划，巡检员定时填报相关数据。与设备故障数据库中历史正常数据进行比对，发现异常自动提示巡检员上报问题。', 3, 1, 100001, '2018-09-19 11:25:42', NULL, NULL);

-- ----------------------------
-- Table structure for geic_product_case
-- ----------------------------
DROP TABLE IF EXISTS `geic_product_case`;
CREATE TABLE `geic_product_case`  (
  `case_id` int(11) NOT NULL AUTO_INCREMENT COMMENT '客户案例ID',
  `product_id` int(11) NOT NULL COMMENT '产品ID',
  `case_title` varchar(100) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '客户案例标题',
  `case_image` varchar(100) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '客户案例简图',
  `case_content` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '客户案例内容',
  `sort` tinyint(2) NULL DEFAULT NULL COMMENT '排序',
  `status` tinyint(2) NULL DEFAULT NULL COMMENT '状态',
  `create_id` int(11) NULL DEFAULT NULL COMMENT '创建人ID',
  `create_time` datetime NULL DEFAULT NULL COMMENT '创建时间',
  `update_id` int(11) NULL DEFAULT NULL COMMENT '更新人ID',
  `update_time` datetime NULL DEFAULT NULL COMMENT '更新时间',
  PRIMARY KEY (`case_id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 5 CHARACTER SET = utf8 COLLATE = utf8_general_ci COMMENT = '产品-客户案例表' ROW_FORMAT = Compact;

-- ----------------------------
-- Records of geic_product_case
-- ----------------------------
INSERT INTO `geic_product_case` VALUES (1, 300101, '东信北邮机房监控项目', '/upload/image/product/product-1.png', '公司机房对于企业来说是军机重地，公司的各项业务的正常运转都依赖于机房中各服务器等设备的正常运行，因此机房的安全防护尤为重要。通过在机房部署监控摄像头、温湿度传感器及烟火感传感器等，监测机房的各环境数据，通过智能监测平台实时展示给监控人员。当环境数据超出阈值，触发告警机制，通过邮件、短信、微信小程序等方式通知监控人员，以便及时处理突发状况。', 1, 1, 100001, '2018-07-04 14:38:54', NULL, NULL);
INSERT INTO `geic_product_case` VALUES (2, 300102, '广西玉柴设备点检项目', '/upload/image/product/product_c2_1.png', '广西玉柴设备点检项目', 1, 1, 100001, '2018-07-04 14:38:54', NULL, NULL);
INSERT INTO `geic_product_case` VALUES (3, 300103, '广西玉柴集团客户', '/upload/image/product/product_c3_1.png', '为广西玉柴集团客户提供超过50种工业级协议解析能力，并实现100+台工段设备的互联接入和三维数据分析展现\r\n', 1, 1, 100001, '2018-07-04 14:38:54', NULL, NULL);
INSERT INTO `geic_product_case` VALUES (4, 300105, '玉柴跨区域VPN组网及网络安全防护', '/upload/image/product/product_c5_1.png', '通过中国移动SD-WAN产品在玉柴玉林厂区、南宁工程研究院和德国研发中心间搭建跨区域VPN专网，实现跨机构、跨厂区组网和网络管控，对工业数据安全实现有效防护。', 1, 1, 100001, '2018-09-19 11:02:56', NULL, NULL);

-- ----------------------------
-- Table structure for geic_product_feature
-- ----------------------------
DROP TABLE IF EXISTS `geic_product_feature`;
CREATE TABLE `geic_product_feature`  (
  `feature_id` int(11) NOT NULL AUTO_INCREMENT COMMENT '功能描述ID',
  `product_id` int(11) NOT NULL COMMENT '产品ID',
  `feature_title` varchar(100) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '功能描述名称',
  `feature_image` varchar(100) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '功能描述图标',
  `feature_content` text CHARACTER SET utf8 COLLATE utf8_general_ci NULL COMMENT '功能描述内容',
  `sort` tinyint(2) NULL DEFAULT NULL COMMENT '顺序',
  `status` tinyint(2) NULL DEFAULT NULL COMMENT '状态',
  `create_id` int(11) NULL DEFAULT NULL COMMENT '创建人ID',
  `create_time` datetime NULL DEFAULT NULL COMMENT '创建时间',
  `update_id` int(11) NULL DEFAULT NULL COMMENT '更新人ID',
  `update_time` datetime NULL DEFAULT NULL COMMENT '更新时间',
  PRIMARY KEY (`feature_id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 35 CHARACTER SET = utf8 COLLATE = utf8_general_ci COMMENT = '产品-功能描述表' ROW_FORMAT = Compact;

-- ----------------------------
-- Records of geic_product_feature
-- ----------------------------
INSERT INTO `geic_product_feature` VALUES (1, 300101, '环境监测', 'icon-product_', '实时采集和上报各个监测站点的环境指标数据。行业客户可根据自身需要，定义预警阀值、锁定监控重点区域和目标。当环境指标接近或超出预设阀值时，系统自动发送告警信息。', 1, 1, 100001, '2018-07-04 14:38:54', NULL, NULL);
INSERT INTO `geic_product_feature` VALUES (2, 300101, '图像处理', 'icon-product_1', '基于图像分析技术，支持恶劣环境、夜间环境下的成像处理，提供满足全天候监控、人脸识别、人流统计、区域入侵等图像处理功能。', 2, 1, 100001, '2018-07-04 14:38:54', NULL, NULL);
INSERT INTO `geic_product_feature` VALUES (3, 300101, '终端管理', 'icon-product_2', '支持视频监控终端、环境传感器、数据传输单元等终端的统一接入和认证，提供客户自服务界面，实时监控客户侧终端的运行状态，进行终端参数的动态配置。', 3, 1, 100001, '2018-07-04 14:38:54', NULL, NULL);
INSERT INTO `geic_product_feature` VALUES (4, 300101, '融合呈现', 'icon-product_3', '支持传感器数据与视频图像的融合呈现，对不同监测区域的动态实时切换。支持通过GPS/基站定位，实现对移动场景下监控终端的实时定位和地图标记。', 4, 1, 100001, '2018-07-04 14:38:54', NULL, NULL);
INSERT INTO `geic_product_feature` VALUES (5, 300102, '巡检管理', 'icon-product_11', '落实用户设备相关的检查制度，自定义设备巡检分类、项目、指标、异常项，生成巡检模板。', 1, 1, 100001, '2018-07-04 14:38:54', NULL, NULL);
INSERT INTO `geic_product_feature` VALUES (6, 300102, '查看任务', 'icon-product_6', '用户在该模块可以快速查看属于当前用户的所有的工作任务。', 2, 1, 100001, '2018-07-04 14:38:54', NULL, NULL);
INSERT INTO `geic_product_feature` VALUES (7, 300102, '设备定位', 'icon-router', '通过扫描RFID标签、条形码和直接查询三种方式来定位巡检设备，支持现场巡检照片、语音和视频的上传。', 3, 1, 100001, '2018-07-04 14:38:54', NULL, NULL);
INSERT INTO `geic_product_feature` VALUES (8, 300102, '远程协同', 'icon-transfer', ' 支持实时图文上传、即时语音沟通和实时视频查看等功能，支持远程监控设备运行情况，故障诊断，实现远程指导人员维修。', 4, 1, 100001, '2018-07-04 14:38:54', NULL, NULL);
INSERT INTO `geic_product_feature` VALUES (9, 300102, '信息存储', 'icon-product_5', '系统存储设备的入库、发放、报废等信息，便于信息的调取，并提供设备档案信息的快速导入导出功能。', 5, 1, 100001, '2018-07-04 14:38:54', NULL, NULL);
INSERT INTO `geic_product_feature` VALUES (10, 300102, '统计分析', 'icon-product_7', '以KPI的方式对设备状态、工单完成情况进行统计分析，以便了解设备的运行情况，及时处理异常情况。', 6, 1, 100001, '2018-07-04 14:38:54', NULL, NULL);
INSERT INTO `geic_product_feature` VALUES (11, 300104, '为互联网提供增值服务', 'icon-product_8', '可提供VPN、网络安全、上网行为管控和用户自服务等多种增值服务，降低企业成本', 1, 1, 100001, '2018-07-04 14:38:54', NULL, NULL);
INSERT INTO `geic_product_feature` VALUES (12, 300104, '低成本MPLS VPN', 'icon-product_9', '可基于互联网组建低成本MPLS VPN ，兼容其它运营商网络', 2, 1, 100001, '2018-07-04 14:38:54', NULL, NULL);
INSERT INTO `geic_product_feature` VALUES (13, 300104, '为数据专线提供增值服务', 'icon-product_10', '可提供网络安全、上网行为管控、网络可视化管理等多种增值服务。', 3, 1, 100001, '2018-07-04 14:38:54', NULL, NULL);
INSERT INTO `geic_product_feature` VALUES (15, 300104, '构建跨数据中心网络', 'icon-product_4', 'SD-WAN打通了两个云之间的二层网络，服务器迁移不需更改IP地址。', 5, 1, 100001, '2018-07-04 14:38:54', NULL, NULL);
INSERT INTO `geic_product_feature` VALUES (16, 300105, '分钟级VPN组网', 'icon-product_1', '支持IPSec、SSL、GRE、低成本MPLS VPN接入，用户通过集中管理平台远程配置。', 1, 1, 100001, '2018-09-19 10:53:05', NULL, NULL);
INSERT INTO `geic_product_feature` VALUES (17, 300105, '网关云化部署', 'icon-product_2', '通过虚拟化技术构建云网关，部署不同的虚拟机实现路由交换、安全、隧道等多种功能。', 2, 1, 100001, '2018-09-19 10:53:46', NULL, NULL);
INSERT INTO `geic_product_feature` VALUES (18, 300105, '混合云组网', 'icon-product_3', '采用VxLAN技术实现公有云、私有云之间网络连接，实现云端应用的数据传输。', 3, 1, 100001, '2018-09-19 10:53:46', NULL, NULL);
INSERT INTO `geic_product_feature` VALUES (19, 300105, '状态化防火墙', 'icon-product_4', '可以根据流量上下文进行管理和控制，支持报文过滤、地址翻译、状态机调整等功能。', 4, 1, 100001, '2018-09-19 10:53:46', NULL, NULL);
INSERT INTO `geic_product_feature` VALUES (20, 300105, '防病毒', 'icon-product_5', '支持对木马、病毒、蠕虫和其他恶意程序的检测和阻挡以及数据包内容扫描功能', 5, 1, 100001, '2018-09-19 10:53:47', NULL, NULL);
INSERT INTO `geic_product_feature` VALUES (21, 300105, '网关即插即用', 'icon-product_6', '出厂前预先配置网关功能简化网关安装配置过程，开机即可自动连接到管理平台。', 6, 1, 100001, '2018-09-19 10:53:47', NULL, NULL);
INSERT INTO `geic_product_feature` VALUES (24, 300103, 'DTU', 'icon-homepage_1', '<h1>\r\n	功能特点：\r\n</h1>\r\n&nbsp; &nbsp; DTU具备工业级端子排线接线,提供串口数据双向转换，内部封装PPP拨号协议以及TCP/IP协议栈，具备嵌入式操作系统，支持复杂工业环境的安装和部署， 能够为客户设备和数据服务中心/云平台快速搭建通信链路，且满足低耗要求，具备使用简单、建设周期短、成本低的特点。<br />\r\n<br />\r\n<p style=\"margin-left:0in;text-align:left;\">\r\n	<br />\r\n</p>\r\n<h3>\r\n	连线时机可控\r\n</h3>\r\n&nbsp; &nbsp; DTU具有连接次数可控、拦截间隔可控、以及连接中断间隔可控功能，为用户节约流量开销。<br />\r\n<p style=\"margin-left:0in;text-align:left;\">\r\n</p>\r\n<h3>\r\n	唤醒方式灵活\r\n</h3>\r\n&nbsp; &nbsp; 当DTU工作在空闲下线模式时，有2种方式可以唤醒DTU。即本地唤醒式和远程唤醒的方式。&nbsp;<br />\r\n<p style=\"margin-left:0in;text-align:left;\">\r\n</p>\r\n<h3>\r\n	保持永久在线\r\n</h3>\r\n&nbsp; &nbsp; 当长时间没有数据通信时，移动网关将断开DTU与中心的连接，以保持连接不被断开，支持断线自动重连、自动拨号等特点。<br />\r\n<p style=\"margin-left:0in;text-align:left;\">\r\n</p>\r\n<h3>\r\n	远程参数配置\r\n</h3>\r\n&nbsp; &nbsp; 支持云管理平台，数据中心服务器、短信、云平台远程参数配置<br />\r\n', 1, 1, 100001, '2018-07-04 14:38:54', NULL, NULL);
INSERT INTO `geic_product_feature` VALUES (25, 300103, '使能平台', 'icon-homepage_4', '<h2>\r\n	连接使能：\r\n</h2>\r\n<ul class=\'details_list\'>\r\n<li>\r\n	•支持主流工业级协议（如：modbus、profinet），为几百种类型自动化控制器和150种不同驱动器提供连接，实现设备与软件应用间的数据流贯通\r\n</li>\r\n<li>\r\n	•保持传感器、装置、设备与工业云之间双向联通\r\n</li>\r\n<li>\r\n	•源数据统一整合处理，减少网络拥堵，降低设备和系统的资源使用\r\n</li>\r\n<li>\r\n	•支持分散、实时的计算能力和远程“解决方案堆栈”，避免任何与成本安全相关的问题\r\n</li>\r\n<li>\r\n	•标准化SDK，为所有可连接的设备生成REST API，提供双向信息传递、文件传输和应用通道挖掘等能力\r\n</li>\r\n</ul>\r\n<h2>\r\n	管理使能：\r\n</h2>\r\n<ul class=\'details_list\'>\r\n<li>\r\n	•统一界面，支持数百万接入设备管理，传输速率高达千兆\r\n</li>\r\n<li>\r\n	•支持公有云、私有云、混合云部署，使现场设备与云平台无缝协作\r\n</li>\r\n</ul>\r\n<h2>\r\n	开发使能：\r\n</h2>\r\n<ul class=\'details_list\'>\r\n<li>\r\n	•可视化的应用开发环境，“拖放式”的UI构建方式，开发和维护更便捷\r\n</li>\r\n<li>\r\n	•上千种预先构建的应用模型，大大缩短开发周期\r\n</li>\r\n<li>\r\n	•支持存储、搜索、关联和分析所有数据，包括来自IT系统的结构化、来自人的非结构化数据，和来自机器的时间序列数据\r\n</li>\r\n</ul>', 2, 1, 100001, '2018-07-04 14:38:54', NULL, NULL);
INSERT INTO `geic_product_feature` VALUES (26, 300106, '1)	实时对讲', 'icon-product_11', '1对1、1对多的语音呼叫、固定群组呼叫、固定群组通话等多种方式的语音对讲；', 1, 1, 100001, '2018-09-19 11:31:58', NULL, NULL);
INSERT INTO `geic_product_feature` VALUES (27, 300106, '多媒体回传', 'icon-product_11', '图片、文字、语音、视频等多媒体信息回传', 2, 1, 100001, '2018-09-19 11:31:58', NULL, NULL);
INSERT INTO `geic_product_feature` VALUES (28, 300106, '巡检路径规划', 'icon-product_11', '根据管道线路、巡检任务对巡检路径进行规划；', 3, 1, 100001, '2018-09-19 11:31:58', NULL, NULL);
INSERT INTO `geic_product_feature` VALUES (29, 300106, '工单管理', 'icon-product_11', '定义巡检任务，对巡检模式、询价对象、询价规则、巡检人员进行配置', 4, 1, 100001, '2018-09-19 11:31:58', NULL, NULL);
INSERT INTO `geic_product_feature` VALUES (30, 300106, '设备管理', 'icon-product_11', '包括设备档案管理、设备入库管理、设备发放管理、设备调拨管理、设备报废管理', 5, 1, 100001, '2018-09-19 11:31:59', NULL, NULL);
INSERT INTO `geic_product_feature` VALUES (31, 300106, '人员管理', 'icon-product_11', '系统自动记录跟踪作业人员、时间、设备、巡检状况等信息', 6, 1, 100001, '2018-09-19 11:31:59', NULL, NULL);
INSERT INTO `geic_product_feature` VALUES (32, 300106, '统计分析', 'icon-product_11', '以KPI的方式综合统计当前设备资产分类及数量、设备故障信息及人员到岗情况', 7, 1, 100001, '2018-09-19 11:31:59', NULL, NULL);
INSERT INTO `geic_product_feature` VALUES (33, 300106, 'AR远程协同', 'icon-product_11', '基于AR技术实现远程协同，实时将指导指令通过富媒体方式推送至前端人员视野内', 8, 1, 100001, '2018-09-19 11:32:00', NULL, NULL);
INSERT INTO `geic_product_feature` VALUES (34, 300106, '室内精准定位', 'icon-product_11', '室内或园区人员定位达到厘米级精度，实时掌握精准位置信息', 9, 1, 100001, '2018-09-19 11:32:00', NULL, NULL);

-- ----------------------------
-- Table structure for geic_product_intro
-- ----------------------------
DROP TABLE IF EXISTS `geic_product_intro`;
CREATE TABLE `geic_product_intro`  (
  `intro_id` int(11) NOT NULL AUTO_INCREMENT COMMENT '产品介绍ID',
  `product_id` int(11) NOT NULL COMMENT '产品ID',
  `intro_image` varchar(100) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '产品介绍简图',
  `intro_content` text CHARACTER SET utf8 COLLATE utf8_general_ci NULL COMMENT '产品介绍内容',
  `status` tinyint(2) NULL DEFAULT NULL COMMENT '状态',
  `create_id` int(11) NULL DEFAULT NULL COMMENT '创建人ID',
  `create_time` datetime NULL DEFAULT NULL COMMENT '创建时间',
  `update_id` int(11) NULL DEFAULT NULL COMMENT '更新人ID',
  `update_time` datetime NULL DEFAULT NULL COMMENT '更新时间',
  PRIMARY KEY (`intro_id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 8 CHARACTER SET = utf8 COLLATE = utf8_general_ci COMMENT = '产品-产品介绍表' ROW_FORMAT = Compact;

-- ----------------------------
-- Records of geic_product_intro
-- ----------------------------
INSERT INTO `geic_product_intro` VALUES (1, 300101, '/upload/image/product/product-i1-1.png', '<h3>\r\n	方案架构\r\n</h3>\r\n<p class=\"MsoNormal\" style=\"text-indent:21.0pt;\">\r\n	终端层：终端设备统一管理，鉴权认证、信息上报、远程管控、自检修复等。\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:20.45pt;\">\r\n	数据层：对应用数据、用户数据、终端数据、系统数据等进行处理、智能分析等。\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:21.0pt;\">\r\n	业务能力层：如应用管理、策略管理、信息处理、终端管理、日志管理、任务管理、账户管理等，提供第三方平台接口，实现多平台的互联互通。\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:21.0pt;\">\r\n	展现层：提供监控视图、告警视图、运营分析视图等。\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:21.0pt;\">\r\n	<br />\r\n</p>\r\n<h3>\r\n	产品主要优势\r\n</h3>\r\n<p class=\"MsoNormal\" style=\"text-indent:21.0pt;\">\r\n	<span>1. </span>多终端设备接入\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:21.0pt;\">\r\n	<span>2. </span>边缘计算\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:21.0pt;\">\r\n	<span>3. </span>平台联动\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:21.0pt;\">\r\n	<span>4. </span>多终端展示\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:21.0pt;\">\r\n	<span>5. </span>定制化服务\r\n</p>', 1, 100001, '2018-07-25 16:07:55', NULL, NULL);
INSERT INTO `geic_product_intro` VALUES (2, 300102, '/upload/image/product/product-i2.png', '<h3>\r\n	方案架构\r\n</h3>\r\n<p class=\"MsoNormal\" style=\"text-indent:21.0pt;\">\r\n	<p class=\"MsoNormal\" style=\"text-indent:21.0pt;\">\r\n		基础设施层：该层包含网络通讯、服务器、智能感知设备等物联网基础设施。其中针对智能感知设备，平台提供各类视频监控、无人机、工业机器人等设备的兼容接口。\r\n	</p>\r\n	<p class=\"MsoNormal\" style=\"text-indent:21.0pt;\">\r\n		平台层：该层提供了<span>SDK</span>、<span>Socket</span>命令、<span>XML</span>、<span>Web API</span>、<span>Web Service</span>的接入扩展，以及基础信息管理功能。\r\n	</p>\r\n	<p class=\"MsoNormal\" style=\"text-indent:21.0pt;\">\r\n		业务层：分为<span>Web</span>端以及移动端两部分。主要包含设备档案管理、设备巡检、设备保养、设备运维等覆盖设备风险点、危险源、关键部位的各种业务应用，且支持巡检模型配置以及保养模型配置等。\r\n	</p>\r\n	<p class=\"MsoNormal\" style=\"text-indent:21.0pt;\">\r\n		分析展示层：该层生动展示业务各类统计指标信息，支持设备性能、设备质量、设备状态等数据的分析展示。\r\n	</p>\r\n	<p class=\"MsoNormal\" style=\"text-indent:21.0pt;\">\r\n<h3>\r\n		产品主要优势：\r\n</h3>\r\n	</p>\r\n	<p class=\"MsoNormal\" style=\"text-indent:21.0pt;\">\r\n		<span>1. </span>移动智能化 <span></span>\r\n	</p>\r\n	<p class=\"MsoNormal\" style=\"text-indent:21.0pt;\">\r\n		<span>2. </span>远程协同；\r\n	</p>\r\n	<p class=\"MsoNormal\" style=\"text-indent:21.0pt;\">\r\n		<span>3. </span>信息可视化\r\n	</p>\r\n	<p class=\"MsoNormal\" style=\"text-indent:21.0pt;\">\r\n		<span>4. </span>大数据分析\r\n	</p>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:21.0pt;\">\r\n	<span></span>\r\n</p>', 1, 100001, '2018-07-25 14:50:07', 100001, '2018-07-25 14:50:12');
INSERT INTO `geic_product_intro` VALUES (4, 300103, '/upload/image/product/product_i3-1.png', '&nbsp; &nbsp; &nbsp; 工程机械、数控机床等生产设备产生的运行数据，经由DTU汇聚后，实时上传至云端存储。连接使能平台能够对海量异构数据进行标准化处理，快速构建对设备运行状态实时可视化监测等应用服务<br />\r\n<br />\r\n<div class=\"O1\" style=\"margin-left:.31in;text-align:left;\">\r\n<h3>\r\n	•工业DTU&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n</h3>\r\n</div>\r\n<p style=\"margin-left:0in;text-align:left;\">\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; DTU作为运营商网络管理的边界，能够有效拉通设备与工业云之间的数据交互，可将DTU与设备控制器（如：PLC、DCS）相连，快速读取数据，并通过4G/以太网回传至云端。\r\n</p>\r\n<p style=\"margin-left:0in;text-align:left;\">\r\n	<br />\r\n</p>\r\n<div class=\"O1\" style=\"margin-left:0in;text-align:left;\">\r\n	<h3>&nbsp; &nbsp; &nbsp; &nbsp;•使能平台</h3>\r\n</div>\r\n<p style=\"margin-left:0in;text-align:left;\">\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 工业设备品牌/类型众多，所涉及通信协议复杂，连接使能平台具备多种工业设备及物联网协议解析能力，将不同类型源数据统一整合至工业云；同时为用户提供可视化的数据分析、建模环境，提高应用开发速度，降本增效\r\n</p>', 1, 100001, '2018-07-25 16:07:55', NULL, NULL);
INSERT INTO `geic_product_intro` VALUES (5, 300104, '/upload/image/product/product_i4.png', '<h3>\r\n	方案架构\r\n</h3>\r\n<p class=\"MsoNormal\" style=\"text-indent:21.0pt;\">\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:21.0pt;\">\r\n	<p class=\"MsoNormal\" style=\"text-indent:21.0pt;\">\r\n		集中管理平台：集中管理平台可对全网运行的<span>SD-WAN</span>网关进行统一配置与管理，并支持用户自服务，客户可通过自服务门户自行配置网关、查询网关状态，实现异常状态预警、远程管理维护等功能。\r\n	</p>\r\n	<p class=\"MsoNormal\" style=\"text-indent:21.0pt;\">\r\n		<span>SD-WAN</span>网关：<span>SD-WAN</span>网关集成多种网络功能为一体，实现<span>VPN</span>网关、防火墙、<span>UTM</span>、<span>IDS/IPS</span>、<span>DDoS</span>、网络探针、带宽按需分配、流量监控等功能“多合一”承载，有效降低硬件成本。\r\n	</p>\r\n	<p class=\"MsoNormal\" style=\"text-indent:21.0pt;\">\r\n<h3>\r\n		产品主要优势：\r\n</h3>\r\n	</p>\r\n	<p class=\"MsoNormal\" style=\"text-indent:21.0pt;\">\r\n		<span>1</span>、多功能一体化\r\n	</p>\r\n	<p class=\"MsoNormal\" style=\"text-indent:21.0pt;\">\r\n		<span>2</span>、即插即用\r\n	</p>\r\n	<p class=\"MsoNormal\" style=\"text-indent:21.0pt;\">\r\n		<span>3</span>、支持异构网络\r\n	</p>\r\n	<p class=\"MsoNormal\" style=\"text-indent:21.0pt;\">\r\n		<span>4</span>、网关形态多样，适配多种场景\r\n	</p>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:21.0pt;\">\r\n	<span></span>\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:21.0pt;\">\r\n	<span></span> \r\n</p>', 1, 100001, '2018-07-25 16:07:55', NULL, NULL);
INSERT INTO `geic_product_intro` VALUES (6, 300105, '/upload/image/product/product_i5.png', '<p class=\"MsoNormal\" style=\"text-indent:21.0pt;\">\r\n	中国移动<span>SD-WAN</span>产品针对工业企业广域网组网需求，将<span>SDN</span>（软件定义网络）和<span>NFV</span>（网络功能虚拟化）技术应用于企业广域网中，通过云端部署的集中管理平台和位于用户侧的通用硬件网关设备，实现分钟级<span>VPN</span>组网、工业数据安全传输、混合云无缝打通、一点集中管控等功能，为工业企业提供灵活快速部署与可视化统一管理的广域网服务。\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:21.0pt;\">\r\n	<span></span>\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:21.0pt;\">\r\n	方案优势\r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:21.0pt;\">\r\n	<span></span>\r\n</p>\r\n<p class=\"MsoListParagraph\" style=\"margin-left:42.0pt;text-indent:-21.0pt;\">\r\n	·&nbsp;&nbsp;\r\n可视化集中管控\r\n</p>\r\n<p class=\"MsoListParagraph\" style=\"margin-left:42.0pt;text-indent:-21.0pt;\">\r\n	·&nbsp;&nbsp;\r\n网关多功能合一\r\n</p>\r\n<p class=\"MsoListParagraph\" style=\"margin-left:42.0pt;text-indent:-21.0pt;\">\r\n	·&nbsp;&nbsp;\r\n全方位安全防护\r\n</p>\r\n<p class=\"MsoListParagraph\" style=\"margin-left:42.0pt;text-indent:-21.0pt;\">\r\n	·&nbsp;&nbsp;\r\n业务易于部署\r\n</p>', 1, 100001, '2018-09-19 11:23:49', NULL, NULL);
INSERT INTO `geic_product_intro` VALUES (7, 300106, '/upload/image/product/product-i2.png', '<p class=\"MsoNormal\" style=\"text-indent:21.0pt;\">\r\n	<p class=\"MsoNormal\" style=\"text-indent:21.0pt;\">\r\n		智能巡检系统能够通过定制终端，将巡检过程产生的数据（如：表单、图片、位置），通过<span>4G</span>网络上传至云端智能巡检调度平台，同时借助实时流媒体传输技术，与后台技术人员进行音视频互动，及时获得远程技术支持。\r\n	</p>\r\n	<p class=\"MsoNormal\" style=\"text-indent:21.0pt;\">\r\n		<span>&nbsp;</span>\r\n	</p>\r\n	<p class=\"MsoNormal\" style=\"text-indent:21.0pt;\">\r\n		方案优势\r\n	</p>\r\n	<p class=\"MsoNormal\" style=\"text-indent:21.0pt;\">\r\n		<span>&nbsp;</span>\r\n	</p>\r\n	<p class=\"MsoNormal\" style=\"text-indent:21.0pt;\">\r\n		巡检效率大幅提升\r\n	</p>\r\n	<p class=\"MsoNormal\" style=\"text-indent:21.0pt;\">\r\n		巡检结果可追溯\r\n	</p>\r\n	<p class=\"MsoNormal\" style=\"text-indent:21.0pt;\">\r\n		随时掌握巡检人员运动轨迹\r\n	</p>\r\n	<p class=\"MsoNormal\" style=\"text-indent:21.0pt;\">\r\n		人员安全自动告警机制\r\n	</p>\r\n</p>\r\n<p class=\"MsoListParagraph\" style=\"margin-left:42.0pt;text-indent:-21.0pt;\">\r\n	<br />\r\n</p>', 1, 100001, '2018-09-19 11:23:36', NULL, NULL);

-- ----------------------------
-- Table structure for geic_products
-- ----------------------------
DROP TABLE IF EXISTS `geic_products`;
CREATE TABLE `geic_products`  (
  `product_id` int(11) NOT NULL AUTO_INCREMENT COMMENT '产品ID',
  `product_name` varchar(100) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '产品名称',
  `product_type` varchar(100) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '产品分类',
  `product_litimg` varchar(100) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '产品缩略图',
  `product_image` varchar(100) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '产品背景图',
  `product_video_image` varchar(100) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '产品视频预览图',
  `product_video` varchar(100) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '产品视频url',
  `product_desc` varchar(500) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '产品简介',
  `product_content` text CHARACTER SET utf8 COLLATE utf8_general_ci NULL COMMENT '产品内容',
  `keyword` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '关键词',
  `status` tinyint(2) NULL DEFAULT NULL COMMENT '状态0编辑1发布2未发布3删除',
  `create_id` int(11) NULL DEFAULT NULL COMMENT '创建人ID',
  `create_time` datetime NULL DEFAULT NULL COMMENT '创建时间',
  `udpate_id` int(11) NULL DEFAULT NULL COMMENT '更新人ID',
  `update_time` datetime NULL DEFAULT NULL COMMENT '更新时间',
  PRIMARY KEY (`product_id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 300107 CHARACTER SET = utf8 COLLATE = utf8_general_ci COMMENT = '产品表' ROW_FORMAT = Compact;

-- ----------------------------
-- Records of geic_products
-- ----------------------------
INSERT INTO `geic_products` VALUES (300101, '智能监测', '300101', NULL, '/static/pic/product/banner-product1.jpg', NULL, NULL, '智能监测平台结合了最先进的物联网、云计算、传感器摄像头等终端设备，可通过PC、手机客户端、微信小程序等实时显示应用场景的各环境数据，并对异常情况进行告警，以保障企业生产运营的安全预防和全面掌控。', NULL, '智能检测', 1, 100001, '2018-07-04 14:37:54', NULL, NULL);
INSERT INTO `geic_products` VALUES (300102, '智能巡检', '300102', NULL, '/static/pic/product/banner-product1.jpg', NULL, NULL, '基于POC能力为基础，提供定制化巡检流程、人员精准定位、智能巡检机器人、工业AR等高级巡检服务；支持云端自主可控高安全解决方案，支持手机端和web端巡检服务，为工业现场应用提供强大支持。', NULL, '智能巡检', 1, 100001, '2018-07-11 14:37:54', NULL, NULL);
INSERT INTO `geic_products` VALUES (300103, '工业设备互联', '300103', NULL, '/static/pic/product/banner-product2.jpg', NULL, NULL, '通过不同类型通信协议转换，实现设备信号标准化，打通工业互联网最后一公里。同时为用户提供应用开发环境与工具、建立数据模型，从而快速构建设备远程监控、故障诊断等标准化应用\r\n', NULL, '工业设备互联', 1, 100001, '2018-07-05 14:37:54', NULL, NULL);
INSERT INTO `geic_products` VALUES (300104, '工业虚拟专网', '300104', NULL, '/static/pic/product/banner-product1.jpg', NULL, NULL, 'SD-WAN网关集成多种网络功能为一体，实现VPN网关、防火墙、UTM、IDS/IPS、DDoS、网络探针、带宽按需分配、流量监控等功能“多合一”承载。', NULL, '工业虚拟专网', 1, 100001, '2018-07-06 14:37:54', NULL, NULL);
INSERT INTO `geic_products` VALUES (300105, 'SD-WAN', '300104', NULL, '/static/pic/product/banner-product1.jpg', NULL, NULL, 'SD-WAN网关集成多种网络功能为一体，实现VPN网关、防火墙、UTM、IDS/IPS、DDoS、网络探针、带宽按需分配、流量监控等功能“多合一”承载。', '', 'SD-WAN', 1, 100001, '2018-09-19 10:44:37', NULL, NULL);
INSERT INTO `geic_products` VALUES (300106, '智能巡检', '300102', NULL, '/static/pic/product/banner-product1.jpg', NULL, NULL, '结合最先进的物联网、云计算、传感器技术，通过PC、手机客户端、微信小程序等实时显示各环境数据，并对异常情况进行告警，全面掌控企业生产运营情况。', '', '智能巡检', 1, 100001, '2018-09-19 11:21:44', NULL, NULL);

-- ----------------------------
-- Table structure for geic_role
-- ----------------------------
DROP TABLE IF EXISTS `geic_role`;
CREATE TABLE `geic_role`  (
  `role_id` int(11) NOT NULL COMMENT '角色ID',
  `role_name` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '角色名称',
  `role_desc` varchar(100) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '角色描述',
  `create_id` int(11) NULL DEFAULT NULL COMMENT '创建人ID',
  `create_time` datetime NULL DEFAULT NULL COMMENT '创建时间',
  `status` tinyint(2) NULL DEFAULT NULL COMMENT '状态0留用1在用2删除',
  `update_id` int(11) NULL DEFAULT NULL COMMENT '更新人ID',
  `update_time` datetime NULL DEFAULT NULL COMMENT '更新时间',
  PRIMARY KEY (`role_id`) USING BTREE
) ENGINE = InnoDB CHARACTER SET = utf8 COLLATE = utf8_general_ci COMMENT = '系统角色表' ROW_FORMAT = Dynamic;

