-- ----------------------------
-- Table structure for geic_role_menu
-- ----------------------------
DROP TABLE IF EXISTS `geic_role_menu`;
CREATE TABLE `geic_role_menu`  (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '关联ID',
  `role_id` int(11) NOT NULL COMMENT '角色ID',
  `menu_id` int(11) NOT NULL COMMENT '菜单ID',
  `status` tinyint(2) NULL DEFAULT NULL COMMENT '状态：0 删除 1 正常',
  PRIMARY KEY (`id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 1 CHARACTER SET = utf8 COLLATE = utf8_general_ci COMMENT = '角色菜单关系表' ROW_FORMAT = Dynamic;

-- ----------------------------
-- Table structure for geic_role_user
-- ----------------------------
DROP TABLE IF EXISTS `geic_role_user`;
CREATE TABLE `geic_role_user`  (
  `id` int(11) NOT NULL COMMENT '关联ID',
  `role_id` int(11) NOT NULL COMMENT '角色ID',
  `user_id` int(11) NOT NULL COMMENT '用户ID',
  `status` tinyint(2) NULL DEFAULT NULL COMMENT '状态：0 删除 1 正常',
  PRIMARY KEY (`id`) USING BTREE
) ENGINE = InnoDB CHARACTER SET = utf8 COLLATE = utf8_general_ci COMMENT = '角色用户关系表' ROW_FORMAT = Dynamic;

-- ----------------------------
-- Table structure for geic_software
-- ----------------------------
DROP TABLE IF EXISTS `geic_software`;
CREATE TABLE `geic_software`  (
  `software_id` int(11) NOT NULL AUTO_INCREMENT COMMENT '软件应用ID',
  `software_type` varchar(100) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '软件应用所属分类',
  `software_name` varchar(100) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '软件应用名称',
  `software_desc` varchar(500) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '软件应用简介',
  `software_icon` varchar(100) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '软件应用图标',
  `price` decimal(10, 2) NULL DEFAULT NULL COMMENT '价格',
  `software_content` text CHARACTER SET utf8 COLLATE utf8_general_ci NULL COMMENT '软件应用内容',
  `trail_days` int(10) NULL DEFAULT NULL COMMENT '软件可试用天数',
  `qrcode` varchar(100) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '二维码',
  `ontrail_url` varchar(100) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '在线试用url',
  `downtrail_url` varchar(100) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '下载试用url',
  `soft_url` varchar(100) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '软件应用url',
  `trail_type` int(10) NULL DEFAULT NULL COMMENT '试用方式(在线试用、下载试用)',
  `os` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '操作系统',
  `brand` varchar(100) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '品牌',
  `soft_company` varchar(100) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '软件应用供应商',
  `hotline` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '售后电话',
  `email` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '售后邮箱',
  `status` tinyint(2) NULL DEFAULT NULL COMMENT '状态（0删除1发布 2下架、待发布）',
  `create_id` int(11) NULL DEFAULT NULL COMMENT '创建人ID',
  `create_time` datetime NULL DEFAULT NULL COMMENT '创建时间',
  `update_id` int(11) NULL DEFAULT NULL COMMENT '更新人ID',
  `update_time` datetime NULL DEFAULT NULL COMMENT '更新时间',
  PRIMARY KEY (`software_id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 111141 CHARACTER SET = utf8 COLLATE = utf8_general_ci COMMENT = '软件超市表' ROW_FORMAT = Dynamic;

-- ----------------------------
