INSERT INTO `geic_software` VALUES (111134, '100301', '透明工厂', '智造家针对离散型中小制造企业特点推出的一个低成本、高效率的工厂生产执行管理系统，帮助工厂从订单到排产、生产工艺、工序流转、过程管理、物料管理、质量检查、订单发货和数据统计分析的全流程信息化管控，通过工厂信息化、可视化和生产管理水平提升，降低成本。', '/upload/image/software/111134-logo.jpg', -1.00, '<img src=\"/upload/image/software/111134-content.png\"/>', 30, NULL, NULL, NULL, NULL, 500102, NULL, NULL, '广州智造家网络科技有限公司', NULL, NULL, 1, 100001, '2018-09-19 15:32:38', NULL, NULL);
INSERT INTO `geic_software` VALUES (111135, '100202', '图纸云', '\r\n图纸云是智造家推出的一款功能丰富、低成本的规范图纸管理、提高图纸使用者工作效率、有利于企业内部及与供应链高效协作的安全可靠的产品。', '/upload/image/software/111135-logo.png', -1.00, '<img src=\"/upload/image/software/111135-content.png\"/>', 30, NULL, NULL, NULL, NULL, 500102, NULL, NULL, '广州智造家网络科技有限公司', NULL, NULL, 1, 100001, '2018-09-19 15:33:42', NULL, NULL);
INSERT INTO `geic_software` VALUES (111136, '100101', '文档内容管理系统', '连用文档内容管理系统专注为企事业单位提供图文档数据的统一存储管理、安全管理、内容分类管理以及共享协同功能，实现分支机构、各部门、内外网之间用户文档的高效、敏捷协同应用，有效降低IT管理成本，同时通过权限、审计、加密等多种安全防护机制为企事业非结构化数据保驾护航。', '/upload/image/software/111136-logo.png', -1.00, '<img src=\"/upload/image/software/111136-content.jpg\"/>', 30, NULL, NULL, NULL, NULL, 500102, NULL, NULL, '深圳市连用科技有限公司', NULL, NULL, 1, 100001, '2018-09-19 15:34:33', NULL, NULL);
INSERT INTO `geic_software` VALUES (111137, '100104', '消息推送', '移动端消息推送解决方案。', '/upload/image/software/111137-logo.png', -1.00, '<img src=\"/upload/image/software/111137-content.jpg\"/>', 30, NULL, NULL, NULL, NULL, 500102, NULL, NULL, '北京麒麟心通网络技术有限公司', NULL, NULL, 1, 100001, '2018-09-19 15:35:17', NULL, NULL);
INSERT INTO `geic_software` VALUES (111138, '100103', '用友畅捷通好会计', 'W好会计，是为小微企业量身打造的一款专业的云财务saas应用，帮助财务人员通过PC端、手机端、微信端随时随地管理现金银行、发票、往来、报税、经营分析等，高效智能提升小微企业的财务管理水平。', '/upload/image/software/111138-logo.jpg', -1.00, '<p>\r\n	<br />\r\n</p>\r\n<p style=\"margin-left:0cm;\">\r\n	好会计，是为小微企业量身打造的一款专业的云财务saas应用，帮助财务人员通过PC端、手机端、微信端随时随地管理现金银行、发票、往来、报税、经营分析等，高效智能提升小微企业的财务管理水平。\r\n</p>\r\n<p>\r\n	<br />\r\n</p>', 30, NULL, NULL, NULL, NULL, 500102, NULL, NULL, '畅捷通信息技术股份有限公司', NULL, NULL, 1, 100001, '2018-09-19 15:36:20', NULL, NULL);
INSERT INTO `geic_software` VALUES (111139, '100101', '云法务', '云法务应用依托中国移动云SAAS平台，为用户提供基础法律咨询及法律文书生成及审核服务，满足中小企业基本的保障性法律服务需求。', '/upload/image/software/111139-logo.png', -1.00, '\r\n<div class=WordSection1>\r\n\r\n<p class=MsoNormal><strong><span lang=EN-US style=\'font-size:10.5pt;font-family:\r\n\"Helvetica\",\"sans-serif\";color:black;background:white\'><o:p>&nbsp;</o:p></span></strong></p>\r\n\r\n<p class=MsoListParagraph style=\'margin-left:36.0pt;text-indent:-36.0pt;\r\nmso-char-indent-count:0;mso-list:l0 level1 lfo1\'><![if !supportLists]><strong><span\r\nlang=EN-US style=\'font-size:10.5pt;font-family:\"Helvetica\",\"sans-serif\";\r\nmso-fareast-font-family:Helvetica;color:black\'><span style=\'mso-list:Ignore\'>1、<span\r\nstyle=\'font:7.0pt \"Times New Roman\"\'>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n</span></span></span></strong><![endif]><strong><span style=\'font-size:10.5pt;\r\nfont-family:\"微软雅黑\",\"sans-serif\";mso-ascii-font-family:Helvetica;mso-hansi-font-family:\r\nHelvetica;mso-bidi-font-family:Helvetica;color:black;background:white\'>智能文书生成</span></strong><strong><span\r\nlang=EN-US style=\'font-size:10.5pt;font-family:\"Helvetica\",\"sans-serif\";\r\ncolor:black;background:white\'><o:p></o:p></span></strong></p>\r\n\r\n<p class=MsoListParagraph style=\'margin-left:36.0pt;text-indent:0cm;mso-char-indent-count:\r\n0\'><strong><span lang=EN-US style=\'font-size:7.5pt;font-family:\"Helvetica\",\"sans-serif\";\r\ncolor:black;background:white\'><o:p>&nbsp;</o:p></span></strong></p>\r\n<img src=\"/upload/image/software/111139-content1.png\" /> \r\n<p class=MsoListParagraph style=\'margin-left:36.0pt;text-indent:0cm;mso-char-indent-count:\r\n0\'><strong><span lang=EN-US style=\'font-size:10.5pt;font-family:\"Helvetica\",\"sans-serif\";\r\ncolor:black;background:white\'><o:p>&nbsp;</o:p></span></strong></p>\r\n\r\n<p class=MsoListParagraph style=\'margin-left:36.0pt;text-indent:-36.0pt;\r\nmso-char-indent-count:0;mso-list:l0 level1 lfo1\'><![if !supportLists]><strong><span\r\nlang=EN-US style=\'font-size:10.5pt;font-family:\"Tahoma\",\"sans-serif\";\r\nmso-fareast-font-family:Tahoma;font-weight:normal\'><span style=\'mso-list:Ignore\'>2、<span\r\nstyle=\'font:7.0pt \"Times New Roman\"\'>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n</span></span></span></strong><![endif]><strong><span style=\'font-size:10.5pt;\r\nfont-family:\"微软雅黑\",\"sans-serif\";mso-ascii-font-family:Helvetica;mso-hansi-font-family:\r\nHelvetica;mso-bidi-font-family:Helvetica;color:black;background:white\'>法律电话咨询</span></strong><strong><span\r\nlang=EN-US style=\'font-size:10.5pt;font-family:\"Tahoma\",\"sans-serif\";\r\nmso-bidi-font-family:\"Times New Roman\";mso-bidi-theme-font:minor-bidi;\r\nfont-weight:normal\'><o:p></o:p></span></strong></p>\r\n\r\n<p class=MsoListParagraph style=\'text-indent:15.0pt\'><span lang=EN-US\r\nstyle=\'font-size:7.5pt\'><o:p>&nbsp;</o:p></span></p>\r\n<img src=\"/upload/image/software/111139-content2.png\" /> \r\n<p class=MsoListParagraph><span lang=EN-US style=\'font-size:10.5pt\'><o:p>&nbsp;</o:p></span></p>\r\n\r\n<p class=MsoListParagraph style=\'margin-left:36.0pt;text-indent:-36.0pt;\r\nmso-char-indent-count:0;mso-list:l0 level1 lfo1\'><![if !supportLists]><strong><span\r\nlang=EN-US style=\'font-size:10.5pt;font-family:\"Tahoma\",\"sans-serif\";\r\nmso-fareast-font-family:Tahoma;font-weight:normal\'><span style=\'mso-list:Ignore\'>3、<span\r\nstyle=\'font:7.0pt \"Times New Roman\"\'>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n</span></span></span></strong><![endif]><strong><span style=\'font-size:10.5pt;\r\nfont-family:\"微软雅黑\",\"sans-serif\";mso-ascii-font-family:Helvetica;mso-hansi-font-family:\r\nHelvetica;mso-bidi-font-family:Helvetica;color:black;background:white\'>文书审核</span></strong><strong><span\r\nlang=EN-US style=\'font-size:10.5pt;font-family:\"Tahoma\",\"sans-serif\";\r\nmso-bidi-font-family:\"Times New Roman\";mso-bidi-theme-font:minor-bidi;\r\nfont-weight:normal\'><o:p></o:p></span></strong></p>\r\n\r\n<p class=MsoListParagraph style=\'text-indent:15.0pt\'><span lang=EN-US\r\nstyle=\'font-size:7.5pt\'><o:p>&nbsp;</o:p></span></p>\r\n<img src=\"/upload/image/software/111139-content3.png\" /> \r\n<p class=MsoListParagraph><span lang=EN-US style=\'font-size:10.5pt\'><o:p>&nbsp;</o:p></span></p>\r\n\r\n<p class=MsoListParagraph style=\'margin-left:36.0pt;text-indent:0cm;mso-char-indent-count:\r\n0\'><span lang=EN-US style=\'font-size:10.5pt\'><o:p>&nbsp;</o:p></span></p>\r\n\r\n</div>', 30, NULL, NULL, NULL, NULL, 500102, NULL, NULL, '上海百事通信息技术股份有限公司', NULL, NULL, 1, 100001, '2018-09-19 15:40:26', NULL, NULL);
INSERT INTO `geic_software` VALUES (111140, '100101', '云桌面', '云桌面就是基于云计算的虚拟桌面，是在本地具备输入输出设备的硬件环境，而运行环境由云端资源虚拟实现，相关的应用、数据、运算都部署于云端。', '/upload/image/software/111140-logo.png', -1.00, '<div class=WordSection1>\r\n\r\n<p class=MsoNormal><span lang=EN-US style=\'font-size:10.5pt;font-family:\"微软雅黑\",\"sans-serif\";\r\nmso-bidi-font-family:Helvetica;color:black;background:white\'><o:p>&nbsp;</o:p></span></p>\r\n\r\n<p class=MsoNormal style=\'text-indent:21.0pt;mso-char-indent-count:2.0\'><span\r\nstyle=\'font-size:10.5pt;font-family:\"微软雅黑\",\"sans-serif\";mso-bidi-font-family:\r\nHelvetica;color:black;background:white\'>通过使用移动云桌面实现桌面虚拟化，各个组织能够达到事半功倍的效率，采用真正以用户为中心的灵活计算方法。通过将应用程序、数据和操作系统与端点分离，并将这些组件转移到数据中心，您就可以在您的云中进行集中管理。桌面和应用程序虚拟化为<span\r\nlang=EN-US> IT </span>提供了一种更加精简和安全的方法来管理用户和提供可按需访问的敏捷桌面服务。</span><span\r\nlang=EN-US><o:p></o:p></span></span></p>\r\n\r\n<p class=MsoNormal><span lang=EN-US style=\'font-size:10.5pt;font-family:\"微软雅黑\",\"sans-serif\"\'><o:p>&nbsp;</o:p></span></p>\r\n\r\n\r\n\r\n<img src=\"/upload/image/software/111140-content.jpg\"/>\r\n</div>', 30, NULL, NULL, NULL, NULL, 500102, NULL, NULL, '甘肃创意信息技术有限公司', NULL, NULL, 1, 100001, '2018-09-19 15:42:19', NULL, NULL);

-- ----------------------------
-- Table structure for geic_software_charge
-- ----------------------------
DROP TABLE IF EXISTS `geic_software_charge`;
CREATE TABLE `geic_software_charge`  (
  `charge_id` int(11) NOT NULL AUTO_INCREMENT COMMENT '资费主键',
  `software_id` int(11) NULL DEFAULT NULL COMMENT '软件id',
  `charge_image` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '资费图片url',
  `charge_content` text CHARACTER SET utf8 COLLATE utf8_general_ci NULL COMMENT '资费内容',
  `status` tinyint(2) NULL DEFAULT NULL COMMENT '状态（0删除 1正常）',
  `sort` tinyint(2) NULL DEFAULT NULL COMMENT '顺序',
  `create_id` int(11) NULL DEFAULT NULL COMMENT '创建人ID',
  `create_time` datetime NULL DEFAULT NULL COMMENT '创建时间',
  `update_id` int(11) NULL DEFAULT NULL COMMENT '更新人ID',
  `update_time` datetime NULL DEFAULT NULL COMMENT '更新时间',
  PRIMARY KEY (`charge_id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 19 CHARACTER SET = utf8 COLLATE = utf8_general_ci COMMENT = '软件超市-资费详情' ROW_FORMAT = Compact;

