-- ----------------------------
-- Table structure for geic_software_docs
-- ----------------------------
DROP TABLE IF EXISTS `geic_software_docs`;
CREATE TABLE `geic_software_docs`  (
  `doc_id` int(11) NOT NULL AUTO_INCREMENT COMMENT '帮助文档主键',
  `software_id` int(11) NULL DEFAULT NULL COMMENT '软件ID',
  `doc_url` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '帮助文档下载地址',
  `doc_name` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '帮助文档名称',
  `doc_icon` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '帮助文档图标',
  `doc_content` text CHARACTER SET utf8 COLLATE utf8_general_ci NULL COMMENT '帮助文档简述',
  `status` tinyint(2) NULL DEFAULT NULL COMMENT '状态（0删除1正常）',
  `sort` tinyint(2) NULL DEFAULT NULL COMMENT '顺序',
  `create_id` int(11) NULL DEFAULT NULL COMMENT '创建人ID',
  `create_time` datetime NULL DEFAULT NULL COMMENT '创建时间',
  `update_id` int(11) NULL DEFAULT NULL COMMENT '更新人ID',
  `update_time` datetime NULL DEFAULT NULL COMMENT '更新时间',
  PRIMARY KEY (`doc_id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 23 CHARACTER SET = utf8 COLLATE = utf8_general_ci COMMENT = '软件超市-帮助文档' ROW_FORMAT = Compact;

