DROP TABLE IF EXISTS `geic_software_intro`;
CREATE TABLE `geic_software_intro`  (
  `intro_id` int(11) NOT NULL AUTO_INCREMENT COMMENT '应用截图主键',
  `software_id` int(11) NULL DEFAULT NULL COMMENT '软件id',
  `intro_image` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '应用截图url',
