-- ----------------------------
-- Table structure for geic_software_trial
-- ----------------------------
DROP TABLE IF EXISTS `geic_software_trial`;
CREATE TABLE `geic_software_trial`  (
  `trial_id` int(11) NOT NULL AUTO_INCREMENT COMMENT '主键ID',
  `user_id` int(11) NOT NULL COMMENT '用户ID',
  `software_id` int(11) NOT NULL COMMENT '软件应用ID',
  `trail_time` datetime NULL DEFAULT NULL COMMENT '试用时间',
  `software_icon` varchar(100) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '软件应用简图',
  `software_title` varchar(100) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '软件应用标题',
  `software_company` varchar(100) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '软件应用企业',
  `software_desc` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '软件应用简述',
  `software_traildays` int(10) NULL DEFAULT NULL COMMENT '软件可试用天数',
  PRIMARY KEY (`trial_id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 15 CHARACTER SET = utf8 COLLATE = utf8_general_ci COMMENT = '软件在线试用表' ROW_FORMAT = Dynamic;

-- ----------------------------
-- Table structure for geic_solution_application
-- ----------------------------
DROP TABLE IF EXISTS `geic_solution_application`;
CREATE TABLE `geic_solution_application`  (
  `application_id` int(11) NOT NULL AUTO_INCREMENT COMMENT '应用场景ID',
  `solution_id` int(11) NOT NULL COMMENT '解决方案ID',
  `application_title` varchar(100) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '应用场景标题',
  `application_image` varchar(100) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '应用场景图标',
  `application_content` text CHARACTER SET utf8 COLLATE utf8_general_ci NULL COMMENT '应用场景内容',
  `sort` tinyint(2) NULL DEFAULT NULL COMMENT '顺序',
  `status` tinyint(2) NULL DEFAULT NULL COMMENT '状态',
  `create_id` int(11) NULL DEFAULT NULL COMMENT '创建人ID',
  `create_time` datetime NULL DEFAULT NULL COMMENT '创建时间',
  `update_id` int(11) NULL DEFAULT NULL COMMENT '更新人ID',
  `update_time` datetime NULL DEFAULT NULL COMMENT '更新时间',
  PRIMARY KEY (`application_id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 56 CHARACTER SET = utf8 COLLATE = utf8_general_ci COMMENT = '解决方案应用场景表' ROW_FORMAT = Compact;

-- ----------------------------
-- Records of geic_solution_application
-- ----------------------------
INSERT INTO `geic_solution_application` VALUES (2, 11, '无人区值守', '/static/pic/solution/appsence.png', '油品在运输、装卸、贮存保管过程中，容易造成人身伤亡和财产损毁，需要加强安全防护措施。<br/><br/>通过对车身、驾驶室、运输罐等多位置安装视频监控终端及环境传感器的安装，实现对驾驶员驾驶行为车辆行驶路径、车辆异常事件（偷漏油、冒烟等）、装卸油操作行为、车辆颠簸状态的实时监控。<br/><br/>有效减少了事故发生的机率，从而大大增加了油品运输途中的安全性，最大程度地预防了可能存在的隐患问题。', 2, 1, 100001, '2018-07-26 16:58:37', NULL, NULL);
