INSERT INTO `geic_solution_application` VALUES (16, 10001, '机械制造', '/upload/image/solutions/1000103.jpg', '针对机械制造行业，通过MES解决方案，实现生产车间作业情况的实时监控以及数据的采集，规范生产订单从创建到执行的标准化流程，实现生产一体化管理。', 1, 1, NULL, NULL, NULL, NULL);
INSERT INTO `geic_solution_application` VALUES (17, 10001, '电子制造', '/upload/image/solutions/1000101.jpg', '针对电子制造，利用条码扫描技术、数字连接终端、设备芯片存储等数据采集手段，实时获取生产现场在制品与物料品质信息。实现企业对生产过程的实时监控与全面追溯需求。 ', 2, 1, NULL, NULL, NULL, NULL);
INSERT INTO `geic_solution_application` VALUES (18, 10001, '汽车制造', '/upload/image/solutions/1000105.jpg', '针对汽车制造，以作业调度为核心，收集生产过程中的实时信息，并对实时事件及时处 理，同时与计划层和控制层保持双向通信能力，优化生产过程。', 3, 1, NULL, NULL, NULL, NULL);
INSERT INTO `geic_solution_application` VALUES (19, 10001, '航空制造', '/upload/image/solutions/1000102.jpg', '针对航空制造行业，基于MES应用系统，带动计划流、物流、信息流、资金流的全面集成，支撑生产精益化和交付质量的提升。', 4, 1, NULL, NULL, NULL, NULL);
INSERT INTO `geic_solution_application` VALUES (20, 10001, '其他离散制造业', '/upload/image/solutions/1000104.jpg', '针对其他离散制造行业，实现对生产过程的管理，使工厂能够对生产过程做出快速响应，减少无附加价值的生产活动。', 5, 1, NULL, NULL, NULL, NULL);
INSERT INTO `geic_solution_application` VALUES (21, 10087, '生产预处理', NULL, '<p class=MsoNormal style=\'text-indent:21.0pt\'><span style=\'background:white\'>调度生产预处理所需的各种资源协调原料、能源、装置检修维护、工艺等与生产有关的运作关系，建立规范的预处理工艺流程，优化生产</span><span\r\nstyle=\'mso-bidi-font-size:10.5pt;line-height:150%;mso-ascii-font-family:宋体;\r\nmso-hansi-font-family:宋体;mso-bidi-font-family:宋体;mso-font-kerning:0pt\'>。</span><span\r\nlang=EN-US style=\'background:white\'><o:p></o:p></span></p>', 1, 1, 100001, '2018-09-13 16:36:45', NULL, NULL);
INSERT INTO `geic_solution_application` VALUES (22, 10087, '反应过程', NULL, '<p class=MsoNormal style=\'text-indent:21.0pt\'><span style=\'background:white\'>对化学反应设备、化学药剂配比与工艺指标实时数据统计分析</span><span\r\nstyle=\'mso-ascii-font-family:等线;mso-ascii-theme-font:minor-fareast;mso-hansi-font-family:\r\n等线;mso-hansi-theme-font:minor-fareast;background:white\'>，</span><span\r\nstyle=\'background:white\'>通过趋势、超标统计等方式，记录设备工艺指标执行情况，为精细化管理提供依据。<span lang=EN-US><o:p></o:p></span></span></p>', 2, 1, 100001, '2018-09-13 16:36:45', NULL, NULL);
INSERT INTO `geic_solution_application` VALUES (23, 10087, '物料分离', NULL, '<p class=MsoNormal style=\'text-indent:21.0pt\'><span style=\'background:white\'>对化学反应设备、化学药剂配比与工艺指标实时数据统计分析</span><span\r\nstyle=\'mso-ascii-font-family:等线;mso-ascii-theme-font:minor-fareast;mso-hansi-font-family:\r\n等线;mso-hansi-theme-font:minor-fareast;background:white\'>，</span><span\r\nstyle=\'background:white\'>通过趋势、超标统计等方式，记录设备工艺指标执行情况，为精细化管理提供依据。<span lang=EN-US><o:p></o:p></span></span></p>', 3, 1, 100001, '2018-09-13 16:36:45', NULL, NULL);
INSERT INTO `geic_solution_application` VALUES (24, 10088, '污染物浓度监测', NULL, '实时采集水质/气体传感器数据，检测害物质浓度，并进行针对性治理及预防，帮助企业有效的控制污染物排放', 1, 1, 100001, '2018-09-13 16:52:49', NULL, NULL);
INSERT INTO `geic_solution_application` VALUES (25, 10088, '设备监控', NULL, '实时可视化监控排污设备的运行状态（如：启停、负荷），异常报警提醒（如：设备故障、测量液位超高/超低报警）', 2, 1, 100001, '2018-09-13 16:52:49', NULL, NULL);
INSERT INTO `geic_solution_application` VALUES (26, 10089, '油气管道巡检', NULL, '针对油气管道巡检业务，通过管道巡检自动化实现工单实时推送、巡检路线导航、巡检人员轨迹监测，异常实时上报等，提高巡检效率，保障管道安全运行。', 1, 1, 100001, '2018-09-13 17:03:03', NULL, NULL);
INSERT INTO `geic_solution_application` VALUES (27, 10089, '站场巡检', NULL, '针对油气站场日常巡检业务，通过管道巡检自动化实现站场设备、阀门等设备日常巡检工单自动推送、巡检计划自动编制、设备异常统计等，提高安全生产效率。', 2, 1, 100001, '2018-09-13 17:03:03', NULL, NULL);
INSERT INTO `geic_solution_application` VALUES (28, 10090, '无人值守', NULL, '通过对油气水井、注水站、集输站、处理站、联合站等生产过程数据的实时监测，实现无人值守，及时告警，可有效减少巡视人员工作。', 1, 1, 100001, '2018-09-13 17:08:48', NULL, NULL);
INSERT INTO `geic_solution_application` VALUES (29, 10090, '远程监控', NULL, '通过智能手机、iPad等移动终端，随时浏览各生产设备生产流程画面、运行参数趋势图以及生产报表，实时了解油田的生产运行情况。', 2, 1, 100001, '2018-09-13 17:08:49', NULL, NULL);
INSERT INTO `geic_solution_application` VALUES (30, 10090, 'KPI统计分析', NULL, '领导层可以及时掌握产油量、百米吨液耗电、采油指数、机采系统效率、原油库存等重要生产指标，了解企业生产运行情况。', 3, 1, 100001, '2018-09-13 17:08:49', NULL, NULL);
INSERT INTO `geic_solution_application` VALUES (31, 10090, '生产调度', NULL, '与移动终端集成电视、电话会议系统，实现远程异地协作，实现动态调度生产并形成考核记录。', 4, 1, 100001, '2018-09-13 17:08:49', NULL, NULL);
INSERT INTO `geic_solution_application` VALUES (32, 10091, '智能管理', NULL, '全生命周期的设备档案管理，让发电机厂家对于设备历史了如指掌，随时可查可控维护数据。', 1, 1, 100001, '2018-09-13 17:13:35', NULL, NULL);
INSERT INTO `geic_solution_application` VALUES (33, 10091, '智能监控', NULL, '实时监控发电机组运行状况，实现发电机组故障实时监控、远程诊断、维护派单等功能。', 2, 1, 100001, '2018-09-13 17:13:35', NULL, NULL);
INSERT INTO `geic_solution_application` VALUES (34, 10091, '智能售后', NULL, '以发电机的业务流程为基础，并结合设备智能运行监控，做到售后派单以及配件调配管理。', 3, 1, 100001, '2018-09-13 17:13:35', NULL, NULL);
INSERT INTO `geic_solution_application` VALUES (35, 10092, '钢铁冶炼', NULL, '针对钢铁冶炼生产现场，实现给水泵站、煤气柜、煤气加压站、煤气放散塔、变配电所等现场自动化装置在线诊断及优化控制，实现生产状况集中监控以及能源生产调度管理。', 1, 1, 100001, '2018-09-13 17:27:09', NULL, NULL);
INSERT INTO `geic_solution_application` VALUES (36, 10092, '铜、锡、铝等有色金属冶炼', NULL, '针对有色金属冶炼企业，采用能源管理系统，实现企业生产所用水、电、气等数据的采集和监控。', 2, 1, 100001, '2018-09-13 17:27:10', NULL, NULL);
INSERT INTO `geic_solution_application` VALUES (37, 10093, '振动设备', NULL, '在振动筛的侧板和激振器上采用振动加速度传感器，可以监测振动筛的振动模式，实现数据实时监测和数据采集功能。', 1, 1, 100001, '2018-09-13 17:31:52', NULL, NULL);
INSERT INTO `geic_solution_application` VALUES (38, 10093, '风机设备', NULL, '在助燃风机和排烟风机的主电机驱动轴承上采用振动传感器，用于远程监测电机的工况，来代替人工巡检、点检，避免点检人员进入煤气现场的风险。', 2, 1, 100001, '2018-09-13 17:31:52', NULL, NULL);
INSERT INTO `geic_solution_application` VALUES (39, 10093, '通用类机电设备', NULL, '针对其他机电设备，实现机电设备运行状况的实时监测，通过数据分析，实现预测性报警。', 3, 1, 100001, '2018-09-13 17:31:52', NULL, NULL);
INSERT INTO `geic_solution_application` VALUES (40, 10094, '工业供给水泵阀设备监控', NULL, '提供水池水位、管道压力、液位、流量等在线监测，图形化动态展示供水泵阀运行状态。', 1, 1, 100001, '2018-09-13 17:43:24', NULL, NULL);
INSERT INTO `geic_solution_application` VALUES (41, 10094, '工业用水环节泵阀远程管控', NULL, '对工业生产、用水输配环节进行智能调度，及时处理故障预警信息。', 2, 1, 100001, '2018-09-13 17:43:24', NULL, NULL);
INSERT INTO `geic_solution_application` VALUES (42, 10095, '换热站供热量动态调节', NULL, '提供换热站内供热量流量控制、开度控制、分时调借控制等动态调节机制，实现热量动态调节控制按照不同的建筑类型进行分时调节控制，例如：办公区周六日和节假日，按照特定曲线运行，维持低供热参数，可以节约热量，降低运行费用。', 1, 1, 100001, '2018-09-13 18:13:48', NULL, NULL);
INSERT INTO `geic_solution_application` VALUES (43, 10095, '热力公司能耗分析', NULL, '采集、计算、分析各各换热站的平均 能耗（每平方建筑面积的耗热量、耗电量、耗水量），实现基础热力管理功能，包括热力的计划管理、热力数据的统计和分析，计算用户的能耗和能耗平衡，及时发现供热问题，为热力公司节约能源，降低运行费用。', 2, 1, 100001, '2018-09-13 18:13:48', NULL, NULL);
INSERT INTO `geic_solution_application` VALUES (44, 10095, '城市热网综合管理', NULL, '建立基于GIS的城市热网管理信息系统，并且与监控调度系统无缝集成，通过地理系统实时查看各种生产数据，实现对城市热网综合管理，能随时掌握全市输配系统的最新资料，供有关部门管理集中供热管网、规划设计、运行调度、决策使用。', 3, 1, 100001, '2018-09-13 18:13:48', NULL, NULL);
INSERT INTO `geic_solution_application` VALUES (45, 10096, '远程设备监测', NULL, '实时采集发电数据（如：发电量、发电功率、电流、电压），远程监控发电设备运行状态，当出现设备异常，及时报警并定位故障，降低停机时间', 1, 1, 100001, '2018-09-13 18:47:37', NULL, NULL);
INSERT INTO `geic_solution_application` VALUES (46, 10096, '环境指标监测', NULL, '连接温控仪、环境监测仪等多个设备，实时监控电站周边环境，多方位掌握电站运行状况', 2, 1, 100001, '2018-09-13 18:47:37', NULL, NULL);
INSERT INTO `geic_solution_application` VALUES (47, 10096, '效益预测评估', NULL, '根据历史业务数据、模型预测电站未来收益预测、建设可行性评估', 3, 1, 100001, '2018-09-13 18:47:37', NULL, NULL);
INSERT INTO `geic_solution_application` VALUES (49, 10097, '风机选址', NULL, '综合考虑气象条件及社会自然条件，如风能质量，盛行风向，自然灾害，地势，候鸟迁徙路线等因素，选择最佳建设地址', 1, 1, 100001, '2018-09-13 18:50:47', NULL, NULL);
INSERT INTO `geic_solution_application` VALUES (50, 10097, '风场设备统一监控', NULL, '针对单机，单一风场（包括风机和设备），及多个风场组合的多层统一监控', 2, 1, 100001, '2018-09-13 18:50:47', NULL, NULL);
INSERT INTO `geic_solution_application` VALUES (51, 10097, '故障分析', NULL, '针对实时/历史的数据通过图形，报表，趋势等多种方式进行展示，实现对风电设备的实时监控与故障诊断。', 3, 1, 100001, '2018-09-13 18:50:47', NULL, NULL);
INSERT INTO `geic_solution_application` VALUES (52, 10097, '功率预测', NULL, '风机功率的精细化预测，需要考虑包括风速，空气密度梯度等一系列复杂因素', 4, 1, 100001, '2018-09-13 18:50:47', NULL, NULL);
INSERT INTO `geic_solution_application` VALUES (53, 10088, '预警规则', '', '实时采集环境指标，并自定义预警阀值，当指标接近或超出预设阀值，系统及时上报告警信息', 3, 1, 100001, '2018-09-13 16:52:49', NULL, '2018-09-14 12:02:44');
