-- ----------------------------
-- Table structure for geic_solution_intro
-- ----------------------------
DROP TABLE IF EXISTS `geic_solution_intro`;
CREATE TABLE `geic_solution_intro`  (
  `intro_id` int(11) NOT NULL AUTO_INCREMENT COMMENT '方案介绍ID',
  `solution_id` int(11) NOT NULL COMMENT '解决方案ID',
  `intro_image` varchar(100) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '方案介绍简图',
  `intro_content` text CHARACTER SET utf8 COLLATE utf8_general_ci NULL COMMENT '方案介绍内容',
  `status` tinyint(2) NULL DEFAULT NULL COMMENT '状态',
  `create_id` int(11) NULL DEFAULT NULL COMMENT '创建人ID',
  `create_time` datetime NULL DEFAULT NULL COMMENT '创建时间',
  `update_id` int(11) NULL DEFAULT NULL COMMENT '更新人ID',
  `update_time` datetime NULL DEFAULT NULL COMMENT '更新时间',
  PRIMARY KEY (`intro_id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 26 CHARACTER SET = utf8 COLLATE = utf8_general_ci COMMENT = '解决方案-方案介绍表' ROW_FORMAT = Compact;

