-- Records of geic_solution_intro
-- ----------------------------
INSERT INTO `geic_solution_intro` VALUES (10, 10086, '/static/pic/solution/1.png', '<div class=\'details_subtitle\'>电力远程抄表解决方案</div><p class=\'describe\'>依托中国移动 4G 物联专网，智能巡检满足不同场景下，巡检过程数据的安全回传，同时借助实时流媒体传输技术，巡检人员可主动向调度平台发起音视频呼叫，实时获取远程支持。</p><p class=\'advantage\'>主要功能</p><ul class=\'details_function clearfix\'><li><i>●</i>工单管理</li><li><i>●</i>安全报警</li><li><i>●</i>巡检导航</li><li><i>●</i>人脸识别</li><li><i>●</i>云端存储</li><li><i>●</i>后台坐席轮询</li><li><i>●</i>人员定位、移动轨迹监测</li><li><i>●</i>实时拍照、音视频采集回传</li></ul><p class=\'advantage\'>方案优势</p><ul class=\'details_list\'><li><i>●</i>工业级定制终端</li><li><i>●</i>通过多媒体传输技术，大幅度提升巡检效率</li><li><i>●</i>精准定位，时刻掌握巡检人员运动轨迹</li><li><i>●</i>建立安全报警机制，保证巡检安全</li></ul>', 1, 100001, '2018-07-26 14:56:30', NULL, NULL);
